mapper.select:
select *
from register


        where left(register_dt, 10) between #{stDate} and #{endDate};


        where left(register_dt, 10) = left(#{endDate}, 10)



    where barcode=#{barcode}

        and left(register_dt, 10) between #{stDate} and #{endDate};


        and left(register_dt, 10) = left(#{endDate}, 10)

mapper.update:
UPDATE conclusion
SET figures= #{figures}, last_inspection_dt=now(),note= #{note}, sample_note=#{sampleNote}
WHERE register_code = #{registerCode}
